	precision mediump float;
	varying vec2 vTextureCoord;
	uniform sampler2D uSampler;
	uniform float tick;

	void main(void) {
		vec2 pos = vTextureCoord;
		pos.x = pos.x + tick;
		if (pos.x > 1.0) gl_FragColor = vec4(0);
